function FRBot() {

	this.runDuration = .5;  //Set to stop after this amount of time (in hours)
	this.maxRice = -1;       //Set to a positive number to stop once that much rice has been collected
	this.riceDonated = 0;    //Assuming 10 per correct answer
	this.started = 0;        //Date/time the bot started
	this.timeoutId = 0;      //Used internally
	this.iterations = 0;
	this.paused = false;
	FRthis = this;
	
	var lastQtn,lastType;
	
	this.run = function() {
		this.started = Date.now();
		this.iterations = this.riceDonated = 0;
		this.runLoop();
		if( this.runDuration > 0 )
			setTimeout(function(){FRthis.stop()},1000*60*60*this.runDuration);
	};
	
	this.runLoop = function() {
		/* Answer a question and schedule answering the next one in some random amount of time */
		this.paused = false;
		lastType=null;
		this.getRice();
		this.timeoutId = setTimeout(function(){FRthis.runLoop()}, Math.random()*2600+4200+(Math.random()<.03?(Math.random()*30000):0) );
		this.iterations++;
	};
	
	this.pause = function() {
		this.paused = true;
		clearTimeout(this.timeoutId);
	}
	this.resume = this.runLoop;
	
	this.getRice = function() {
		var type = document.getElementsByClassName("rice-counter__category-text")[0].innerText, Answer;
		var question   = document.getElementsByClassName("card-title")[0].innerHTML.split(" =")[0];
				
		this.riceDonated=parseInt(document.getElementsByClassName("rice-counter__value")[0].innerText);
		
		/* Check for return conditions */
		if( this.maxRice > 0 && this.riceDonated >= this.maxRice)
			return this.stop();	
		
		/* Select an answer to the question presented */
		if( type.indexOf("Chemical Sym")!=-1 ) {
			Answer = ElementLookup[question];
		}
		else if( type.indexOf("Vocab")!=-1 || type.indexOf("Spanish")!=-1 || type.indexOf("Italian")!=-1 || type.indexOf("French")!=-1 || type.indexOf("German")!=-1 ) {
			lastType = "Vocab";
			lastQtn = question.split(" means")[0].toLowerCase();
			Answer = VocabAnswers[lastQtn];
		}
		else if( type.indexOf("World Capitals")!=-1 ) {
			lastType = "Capitals";
			lastQtn = question.split(" is capital of")[0].toLowerCase();
			Answer = CapitalAnswers[lastQtn];
		}
		else if( type.indexOf("Identify Countries")!=-1 || type.indexOf("Flags")!=-1 || type.indexOf("World Landmarks")!=-1 || type.indexOf("Famous Paintings")!=-1 ) {
			lastType = "Img";
			lastQtn = document.getElementsByClassName("question-image")[0].firstChild.src.split('/').pop().split('.')[0].replace(/[^a-z0-9]/gi, '_');
			Answer = ImgAnswerKey[lastQtn];
		}
		
		if( Answer==null )
				Answer = 1 + Math.round(Math.random()*3);
		
		//console.log("Answer="+Answer);
		
		/* Check each possible answer and choose the matching one */
		/* Note: randomly getting a small percentage incorrect to mimic human error */
		var clicked=false;
		Array.from(document.getElementsByClassName("fade-appear-done")).forEach(
			function (curr,index) {
				//console.log("Option "+(index+1)+"="+curr.innerText);
				if(!clicked && (index==3||Math.random()<0.005/4||(Answer!=null&&(Answer==(index+1)||Answer==curr.innerText))||(type=="Multiplication Table"&&curr.innerText==eval(status.replace("x","*"))))) {
					curr.firstChild.click();
					clicked=true;
				}
			}
		);
		setTimeout(function(){FRthis.storeAnswer()},2000);
		
	};
	
	this.storeAnswer = function() {
		/* Have to check answer some time after the click by looking for a 'correct' class added to one of the answers */
		if(lastType!=undefined) {
			var lastAnswer;
			Array.from(document.getElementsByClassName("fade-appear-done")).forEach(
				function (curr,index) {
					if(Array.from(curr.firstChild.classList).indexOf("correct")!=-1)
						lastAnswer=curr.firstChild.innerText;
				}
			);
			//console.log(lastType+"["+lastQtn+"]="+lastAnswer);
			
			if(lastType === "Vocab")
				VocabAnswers[lastQtn]=lastAnswer;
			else if(lastType === "Capitals")
				CapitalAnswers[lastQtn] = lastAnswer;
			else if(lastType === "Img")
				ImgAnswerKey[lastQtn] = lastAnswer;
		}
	}
	
	this.stop = function() {
		clearTimeout(this.timeoutId);
		this.started = 0;
	};
	
	this.status = function() {
		if( this.started != 0 ) {
			var info = this.paused ? '(PAUSED)\n' : '';
			var elapsedMinutes = ( Date.now() - this.started ) / ( 1000 * 60 );
			info = info.concat( 'Running for ' );
			if( elapsedMinutes <= 59 )
				info = info.concat( Math.floor(elapsedMinutes) + ' minutes\n' );
			else
				info = info.concat( (elapsedMinutes / 60).toFixed(2) + ' hours\n' );
			info = info.concat( 'Rice donated: ' + this.riceDonated + '\n' );
			info = info.concat( 'Questions seen: ' + this.iterations + '\n' );
			if( this.maxRice != -1 )
				info = info.concat( 'Max rice to collect: ' + this.maxRice + '\n' );
			if( this.runDuration > 0 ) {
				var timeLeft = this.runDuration * 60 - elapsedMinutes ;
				if( timeLeft >= 60 )
					info = info.concat( 'Time left: ' + ( timeLeft / 60 ).toFixed(2) + ' hours\n' );
				else 
					info = info.concat( 'Time left: ' + timeLeft.toFixed(2) + ' minutes\n' );
			}
			console.log( info );
		}
		else
			console.log( 'Stopped' );
	}
};

